<?phpfunction mashable_style_social_share(){	$options = get_option('msssh_options');		global $post;	if($options['enable']) {		$msssh_content = '<aside class="msssh-container" ><div id="shareme" data-url="'.get_permalink().'" data-text="'.get_the_title().'"</div></aside>';	}	return $msssh_content;}add_filter('the_content', 'msssh_check_conditions');function msssh_check_conditions($content){	$options = get_option('msssh_options');		if($options['msssh_embed'] === "auto_embed") 	{		if($options['show_single'] && is_single()) {			if($options['above_below_post'])			{			return mashable_style_social_share().$content;		    }		return $content.mashable_style_social_share();			}		elseif($options['show_page'] && is_page()) 		{			if($options['above_below_page'])			{			return mashable_style_social_share().$content;		}		return $content.mashable_style_social_share();				}				return $content;	}else{		return $content;	}	}function msssh_social_share(){	$options = get_option('msssh_options');	if($options['msssh_embed'] === "template_code") {		return mashable_style_social_share();	}}// short code//add_shortcode('msssh_social_share', 'msssh_social_share');?>